mapper.update:
update cinebox_cscenter
set cscenter_title = #{cscenter_title},
cscenter_content = #{cscenter_content},
cscenter_reg_date = SYSDATE,
cscenter_type = #{cscenter_type}
where cscenter_number =#{cscenter_number}
and member_id =#{member_id}

mapper.select:


select count(cscenter_number)
from cinebox_cscenter 
where cscenter_number>0 and cscenter_type=#{searchType}

mapper.insert:
insert into cinebox_cscenter (cscenter_number, member_id, cscenter_title, cscenter_content, cscenter_hit, cscenter_group, cscenter_step, cscenter_indent, cscenter_type, cscenter_headline) values (cscenter_seq.nextval, #{member_id}, #{cscenter_title}, #{cscenter_content}, '0',
#{cscenter_number}
#{cscenter_group}
, '1', '1', #{cscenter_type}, #{cscenter_headline})
